/*
To alter the location of an External Data Source in Azure SQL Database or SQL Server, there isn't a direct ALTER 
statement for it. Instead, you would need to drop the existing external data source and recreate it with the new location.
*/
USE Sales;
GO;


DROP EXTERNAL DATA SOURCE sales_data;
GO

CREATE EXTERNAL DATA SOURCE sales_data
WITH (
    LOCATION = 'https://datalake8heknbv.dfs.core.windows.net/files/'
);
GO

-- Database for sales data
CREATE DATABASE Sales
  COLLATE Latin1_General_100_BIN2_UTF8;
GO;

Use Sales;
GO;

-- External data is in the Files container in the data lake
CREATE EXTERNAL DATA SOURCE sales_data WITH (
    LOCATION = 'https://datalake8heknbv.dfs.core.windows.net/OutputFiles/SalesData'
);
GO;

-- Format for table files
CREATE EXTERNAL FILE FORMAT ParquetFormat
    WITH (
            FORMAT_TYPE = PARQUET,
            DATA_COMPRESSION = 'org.apache.hadoop.io.compress.SnappyCodec'
        );
GO;

